contracts/Chorus.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "./OracleGetter.sol";
import "./ERC20.sol";
import "./Inflation.sol";
import "hardhat/console.sol";

/** 
 @author Tellor Inc.
 @title Chorus
 @dev Chorus is a structure for issuing semi-stablecoins as community currencies
**/
// slither-disable-next-line missing-inheritance
contract Chorus is Inflation, OracleGetter, ERC20 {
    /*Events*/
    event CollateralThreshold(uint256 _collateralThreshold);//emits if collateral threshold changes
    event CollateralPriceAge(uint256 _collateralPriceAge);//emits if collateral price age changes
    event Liquidate(
        address _party,
        uint256 _tokensAmnt,
        uint256 _collateralAmnt,
        uint256 _collateralPenalty
    );//emits upon a liquidation
    event LiquidationPenatly(uint256 _newPenalty);//emits when the liquidation penalty changes
    event MintTokens(
        address _holder,
        uint256 _amount,
        address _to,
        uint256 _collateralRatio
    );//emits when new tokens are minted
    event NewAdmin(address _newAdmin);//emits when a new admin is set
    event WithdrawCollateral(
        address _holder,
        uint256 _collateralAmnt,
        uint256 _collateralRatio
    );//emits when collateral is withdrawn
    event WithdrawToken(address _holder, uint256 _tokenAmnt, uint256 _collateralAmnt);//emits when tokens are withdrawn
    event WithdrawTokenRequest(address _user, uint256 _amount);

    /*Variables*/
    struct WithdrawDetails{
        uint256 amount;
        uint256 requestDate;
    }
    ERC20 public collateralToken;
    address public admin = msg.sender;
    uint256 private tknPrice = 1e18;
    uint256 public collateralID; // The collateral id used to check the Tellor oracle for its USD price.
    uint256 public collateralPriceGranularity;
    uint256 public collateralThreshold = 15e17; // 150%.
    uint256 public collateralPriceAge = 3600; // e.g. 1hr.  This is the delay in the feed from Tellor
    uint256 public liquidationPenatly = 0;
    uint256 public inflRatePerSec;// The rate at which the token decreases value. 1e18 precision. 100e18 is 100%.
    uint256 public inflLastUpdate = block.timestamp;
    address public inflBeneficiary; // Where to send the inflation tokens.
    mapping(address => WithdrawDetails) withdrawRequested;

    /*Modifiers*/
    modifier onlyAdmin {
        require(msg.sender == admin, "not an admin");
        _;
    }

    modifier within100e18Range(uint256 _value) {
        require(_value > 0 && _value < 100e18, "value not within allowed limits");
        _;
    }

    /*Functions*/
    /**
     * @dev This is the constructor, sets the inital paramaters in the system
     * The parameters include, the Tellor Address, collateral token's address,
     * collateral token's requestID, the price granualrity, the token name, 
     * token's symbol, inflation rate per year, and the inflation beneficiary 
     */
    constructor(
        address payable _tellorAddress,
        address _collateralToken,
        uint256 _collateralID,
        uint256 _collateralPriceGranularity,
        string memory _tokenName,
        string memory _tokenSymbol,
        uint256 _inflRatePerYear,
        address _inflBeneficiary,
        bool _isWhitelisted
    )
        OracleGetter(_tellorAddress)
        ERC20(_tokenName, _tokenSymbol,_isWhitelisted)
        within100e18Range(_inflRatePerYear)
    {
        collateralID = _collateralID;
        collateralToken = ERC20(_collateralToken);
        collateralPriceGranularity = _collateralPriceGranularity;
        require(_collateralPriceGranularity > 0 && _collateralPriceGranularity <= 1e18, "value not within allowed limits");
        require(_inflBeneficiary != address(0), "benificiary address not set");
        inflBeneficiary = _inflBeneficiary;
        inflRatePerSec = yearlyRateToPerSec(_inflRatePerYear);
    }

    /**
     * @dev Returns the current token price in USD reduced by the current inflation.
     * @return uint256 token price
     */
    function collateralBalance() external view returns (uint256) {
        return collateralToken.balanceOf(address(this));
    }

    /**
     * @dev Checks the Tellor oracle and gets the collateral price
     * @return uint256 collateral price in USD upscaled to e18 precision.
     */
    // slither-disable-next-line timestamp
    function collateralPrice() public view returns (uint256) {
        (bool _didGet, uint256 _collateralPrice, ) =
            _getDataBefore(collateralID, block.timestamp - collateralPriceAge);
        require(_didGet, "getting oracle price");
        return mul(_collateralPrice, div(1e18, collateralPriceGranularity));
    }

    /**
     * @dev A view funciton to look at the collateralization of the system
     * @return uint256 collateral token balance of this address / totalSupply
     */
    function collateralRatio() public view returns (uint256) {
        uint256 _collateralBalance = collateralToken.balanceOf(address(this));
        // slither-disable-next-line incorrect-equality
        if(totalSupply() == 0 || _collateralBalance == 0) {
            return 0;
        }
        uint256 _collateralValue = wmul(collateralPrice(), _collateralBalance);
        uint256 tokenSupplyWithInflInterest =
            accrueInterest(totalSupply(), inflRatePerSec, block.timestamp - inflLastUpdate);
        uint256 _tokenValue = wmul(tokenPrice(), tokenSupplyWithInflInterest);
        return add(1e18, wdiv(_tokenValue, _collateralValue));
    }

    /**
     * @dev Allows the admin to deposit collateral
     * @param _amount the amount of collateral token to deposit
     */
    function depositCollateral(uint256 _amount) external onlyAdmin {
        require(_amount > 0, "deposit amount 0");
        require(
            collateralToken.transferFrom(msg.sender, address(this), _amount),
            "failed collateral deposit transfer"
        );
    }

    /**
     * @dev Function to allow anyone to liquidate the system if it is undercollateralized
     */
    function liquidate() external {
        require(
            collateralRatio() > collateralThreshold,
            "collateral utilizatoin is below threshold"
        );
        require(balanceOf(msg.sender) > 0, "msg sender doesn't own any tokens");
        uint256 _tknSuplyRatio =
            wdiv(collateralToken.balanceOf(address(this)), totalSupply());
        uint256 _tokensToBurn = balanceOf(msg.sender);
        uint256 _collatAmt = wmul(_tokensToBurn, _tknSuplyRatio);
        uint256 _collatPenalty = wmul(_collatAmt, liquidationPenatly);
        emit Liquidate(msg.sender, _tokensToBurn, _collatAmt, _collatPenalty);
        _burn(msg.sender, _tokensToBurn);
        require(
            collateralToken.transfer(msg.sender, sub(_collatAmt, _collatPenalty)),
            "collateral liquidation transfer fails"
        );
        require(
            collateralToken.transfer(inflBeneficiary, _collatPenalty),
            "collateral liquidation penalty transfer fails"
        );
    }

    /**
     * @dev Allows the admin to mint tokens up to the collateral threshold
     * @param _amount the amount of collateral tokens to mint
     * @param _to   the address to mint them to;
     */
    function mintToken(uint256 _amount, address _to) external onlyAdmin {
        _mint(_to, _amount);
        uint256 _cRatio = collateralRatio();
        require(
            _cRatio < collateralThreshold,
            "collateral utilization above the threshold"
        );
        emit MintTokens(msg.sender, _amount, _to, _cRatio);
    }

     /**
     * @dev Allows a user to request to withdraw tokens
     * @param _amount the amount of tokens to withdraw
     */
    function requestWithdrawToken(uint256 _amount) external {
        require(_amount > 0, "amount should be greater than 0");
        require(balanceOf(msg.sender) >= _amount, "not enough balance");
        withdrawRequested[msg.sender].requestDate = block.timestamp;
        withdrawRequested[msg.sender].amount = _amount;
        _transfer(msg.sender, address(this), _amount);
        emit WithdrawTokenRequest(msg.sender, _amount);
    }

    /**
     * @dev Allows the user to set a new admin address
     * @param _newAdmin the address of the new admin address
     */
    function setAdmin(address _newAdmin) external onlyAdmin {
        require(_newAdmin != address(0), "cannot send to the zero address");
        admin = _newAdmin;
        emit NewAdmin(_newAdmin);
    }

    /**
     * @dev Allows the admin to set the collateral price Age (delay in feed from Tellor)
     * @param _amount the amount of delay in the price feed (we want to wait for disputes)
     */
    function setCollateralPriceAge(uint256 _amount) external onlyAdmin {
        collateralPriceAge = _amount;
        emit CollateralPriceAge(_amount);
    }

    /**
     * @dev Allows the admin to set the Collateral threshold
     * @param _amount new collateral threshold
     */
    function setCollateralThreshold(uint256 _amount)
        external
        onlyAdmin
        within100e18Range(_amount)
    {
        collateralThreshold = _amount;
        emit CollateralThreshold(_amount);
    }

    /**
     * @dev Allows the admin to set the liquidation penalty
     * @param _amount the amount of the liquidation penalty
     */
    function setLiquidationPenatly(uint256 _amount)
        external
        onlyAdmin
        within100e18Range(_amount)
    {
        liquidationPenatly = wdiv(_amount, 100e18); // Convert to a fraction.
        emit LiquidationPenatly(liquidationPenatly);
    }

    /**
     * @dev Returns the current token price in USD reduced by the current inflation.
     * @return uint256 token price
     */
    function tokenPrice() public view returns (uint256) {
        return
            accrueInflation(
                tknPrice,
                inflRatePerSec,
                block.timestamp - inflLastUpdate
            );
    }

    /**
     * @dev Function to reduce token price by the inflation rate,
     * increases the total supply by the inflation rate and
     * sends the new tokens to the inflation beneficiary.
     */
    // slither-disable-next-line timestamp
    function updateInflation() external {
        uint256 secsPassed = block.timestamp - inflLastUpdate;
        require(secsPassed > 0, "no inflation increase yet");
        inflLastUpdate = block.timestamp;
        tknPrice = accrueInflation(tknPrice, inflRatePerSec, secsPassed);
        uint256 _tokensToMint =
            sub(
                accrueInterest(totalSupply(), inflRatePerSec, secsPassed),
                totalSupply()
            );
        _mint(inflBeneficiary, _tokensToMint);
    }

    /**
     * @dev Allows the admin to withdraw collateral above the threshold
     * @param _amount the amount of collateral token to deposit
     */
    function withdrawCollateral(uint256 _amount) external onlyAdmin {
        require(
            collateralToken.transfer(msg.sender, _amount),
            "collateral transfer fails"
        );
        uint256 _cRatio = collateralRatio();
        require(
            _cRatio < collateralThreshold,
            "collateral utilization above the threshold"
        );
        // slither-disable-next-line reentrancy-events
        emit WithdrawCollateral(msg.sender, _amount, _cRatio);
    }

    /**
     * @dev Allows a user to withdraw tokens
     */
    function withdrawToken() external {
        WithdrawDetails memory wd = withdrawRequested[msg.sender];
        uint256 _amount = withdrawRequested[msg.sender].amount;
        require(_amount > 0, "amount should be greater than 0");
        uint256 _waitPeriod = 1 + 100 * _amount / totalSupply() / 5; //increases by 1 day for every 5 percent
        // slither-disable-next-line timestamp
        require(block.timestamp - wd.requestDate >= 86400 * _waitPeriod, "must wait to withdraw");
        withdrawRequested[msg.sender].amount = 0;
        uint256 _collatPrice = collateralPrice();
        uint256 _priceRatio = wdiv(tokenPrice(), _collatPrice);
        uint256 _collateralAmnt = wmul(_priceRatio, _amount);
        _burn(address(this), _amount);
        require(
            collateralToken.transfer(msg.sender, _collateralAmnt),
            "collateral transfer fail"
        );
        // slither-disable-next-line reentrancy-events
        emit WithdrawToken(msg.sender, _amount, _collateralAmnt);
    }
}

contracts/OracleGetter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "./interfaces/ITellor.sol";

/** 
 @author Tellor Inc.
 @title OracleGetter
 @dev a way to access Tellor variables
**/
contract OracleGetter {
    ITellor private tellor;

    /**
     * @dev Constructor for the oracle getters
     * @param _tellor the tellor address
     */
    constructor(address payable _tellor) {
        tellor = ITellor(_tellor);
    }

    /**
     * @dev Allows the user to get the first value for the requestId before the specified timestamp
     * @param _requestId is the requestId to look up the value for
     * @param _timestamp before which to search for first verified value
     * @return _ifRetrieve bool true if it is able to retreive a value, the value, and the value's timestamp
     * @return _value the value retrieved
     * @return _timestampRetrieved the value's timestamp
     */
    function _getDataBefore(uint256 _requestId, uint256 _timestamp)
        internal
        view
        returns (
            bool _ifRetrieve,
            uint256 _value,
            uint256 _timestampRetrieved
        )
    {
        (bool _found, uint256 _index) =
            _getIndexForDataBefore(_requestId, _timestamp);
        if (!_found) return (false, 0, 0);
        uint256 _time =
            tellor.getTimestampbyRequestIDandIndex(_requestId, _index);
        _value = tellor.retrieveData(_requestId, _time);
        //If value is diputed it'll return zero
        if (_value > 0) return (true, _value, _time);
        return (false, 0, 0);
    }

    /**
     * @dev Internal function to get the index of the Tellor data
     * @param _requestId requestID for Tellor data feed
     * @param _timestamp timestamp of request
     */
    /* solhint-disable */
    function _getIndexForDataBefore(uint256 _requestId, uint256 _timestamp)
        internal
        view
        returns (bool, uint256)
    {
        uint256 _count = tellor.getNewValueCountbyRequestId(_requestId);
        if (_count > 0) {
            uint256 _middle;
            uint256 _start = 0;
            uint256 _end = _count - 1;
            uint256 _time;
            //Checking Boundaries to short-circuit the algorithm
            _time = tellor.getTimestampbyRequestIDandIndex(_requestId, _start);
            if (_time >= _timestamp) return (false, 0);
            _time = tellor.getTimestampbyRequestIDandIndex(_requestId, _end);
            if (_time < _timestamp) return (true, _end);
            //Since the value is within our boundaries, do a binary search
            while (true) {
                _middle = (_end - _start) / 2 + 1 + _start;
                _time = tellor.getTimestampbyRequestIDandIndex(
                    _requestId,
                    _middle
                );
                if (_time < _timestamp) {
                    //get imeadiate next value
                    uint256 _nextTime =
                        tellor.getTimestampbyRequestIDandIndex(
                            _requestId,
                            _middle + 1
                        );
                    if (_nextTime >= _timestamp) {
                        return (true, _middle);//_time is correct
                    } else {
                        //look from middle + 1(next value) to end
                        _start = _middle + 1;
                    }
                } else {
                    uint256 _prevTime =
                        tellor.getTimestampbyRequestIDandIndex(
                            _requestId,
                            _middle - 1
                        );
                    if (_prevTime < _timestamp) {
                        // _prevtime is correct
                        return (true, _middle - 1);
                    } else {
                        //look from start to middle -1(prev value)
                        _end = _middle - 1;
                    }
                }
            }
        }
        return (false, 0);
    }
}

contracts/ERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";

/** 
 @author Open Zeppelin / Tellor
 @title ERC20
 @dev Taken from OZ, but no context
**/
contract ERC20 is IERC20 {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    address whitelistAdmin;
    bool public isWhitelistedSystem;
    mapping(address => uint) public whitelistedAmount;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    event NewWhitelistAdmin(address _newAdmin);
    event NewWhitelistedAmount(address _user, uint256 _amount);

    /**
     * @dev Sets the values for {name} and {symbol}, initializes {decimals} with
     * a default value of 18.
     * To select a different value for {decimals}, use {_setupDecimals}.
     * All three of these values are immutable: they can only be set once during
     * construction.
     */
    constructor(string memory name_, string memory symbol_, bool _isWhitelistedSystem) {
        _name = name_;
        _symbol = symbol_;
        _decimals = 18;
        isWhitelistedSystem = _isWhitelistedSystem;
        whitelistAdmin = msg.sender;
    }

    /**
     * @dev Allows the user to set a new admin address
     * @param _newAdmin the address of the new admin address
     */
    function setWhitelistAdmin(address _newAdmin) external {
         require(msg.sender == whitelistAdmin, "not the whitelist admin");
        require(_newAdmin != address(0), "cannot send to the zero address");
        whitelistAdmin = _newAdmin;
        emit NewWhitelistAdmin(_newAdmin);
    }

    function setWhitelistedAmount(address _user, uint256 _amount) external{
            require(msg.sender == whitelistAdmin, "not the whitelist admin");
            whitelistedAmount[_user] = _amount;
            emit NewWhitelistedAmount(_user,_amount);
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     */
    function decimals() public view virtual returns (uint8) {
        return _decimals;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _balances[account];
    }

    /**
     * @dev See {IERC20-transfer}.
     * Requirements:
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     * Requirements:
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _approve(msg.sender, spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     * Requirements:
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `amount`.
     */
    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     * Emits an {Approval} event indicating the updated allowance.
     * Requirements:
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender] + addedValue
        );
        return true;
    }

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     * Emits an {Approval} event indicating the updated allowance.
     * Requirements:
     * - `spender` cannot be the zero address.
     * - `spender` must have allowance for the caller of at least
     * `subtractedValue`.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            msg.sender,
            spender,
            _allowances[msg.sender][spender] - subtractedValue
        );
        return true;
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
     * This is internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     * Emits a {Transfer} event.
     * Requirements:
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        if (isWhitelistedSystem){
            require(_balances[recipient] + amount <= whitelistedAmount[recipient], "recipient not whitelisted for amount");
            require(_balances[sender] <= whitelistedAmount[sender], "sender not whitelisted for amount");
        }
        _balances[sender] = _balances[sender] - amount;
        _balances[recipient] = _balances[recipient] + amount;
        emit Transfer(sender, recipient, amount);
    }

    /** @dev Creates `amount` tokens and assigns them to `account`, increasing
     * the total supply.
     * Emits a {Transfer} event with `from` set to the zero address.
     * Requirements:
     * - `to` cannot be the zero address.
     */
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");
        _totalSupply = _totalSupply + amount;
        _balances[account] = _balances[account] + amount;
        emit Transfer(address(0), account, amount);
    }

    /**
     * @dev Destroys `amount` tokens from `account`, reducing the
     * total supply.
     * Emits a {Transfer} event with `to` set to the zero address.
     * Requirements:
     * - `account` cannot be the zero address.
     * - `account` must have at least `amount` tokens.
     */
    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");
        _balances[account] = _balances[account] - amount;
        _totalSupply = _totalSupply - amount;
        emit Transfer(account, address(0), amount);
    }

    /**
     * @dev Sets `amount` as the allowance of `spender` over the `owner` s tokens.
     * This internal function is equivalent to `approve`, and can be used to
     * e.g. set automatic allowances for certain subsystems, etc.
     * Emits an {Approval} event.
     * Requirements:
     * - `owner` cannot be the zero address.
     * - `spender` cannot be the zero address.
     */
    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    /**
     * @dev Sets {decimals} to a value other than the default one of 18.
     * WARNING: This function should only be called from the constructor. Most
     * applications that interact with token contracts will not expect
     * {decimals} to ever change, and may work incorrectly if it does.
     */
    function _setupDecimals(uint8 decimals_) internal virtual {
        _decimals = decimals_;
    }
}

contracts/Inflation.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.3;

import "./Math.sol";

// Borrowed from https://github.com/wolflo/solidity-interest-helper.
// Inspired by https://medium.com/coinmonks/math-in-solidity-part-4-compound-interest-512d9e13041b
// Using DSMath from DappHub https://github.com/dapphub/ds-math

/**
 * @title Interest
 * @author Nick Ward
 * @dev Uses DSMath's wad and ray math to implement (approximately)
 * continuously compounding interest by calculating discretely compounded
 * interest compounded every second.
 */
contract Inflation is DSMath {
    //// Fixed point scale factors
    // wei -> the base unit
    // wad -> wei * 10 ** 18. 1 ether = 1 wad, so 0.5 ether can be used
    //      to represent a decimal wad of 0.5
    // ray -> wei * 10 ** 27

    // Go from wad (10**18) to ray (10**27)
    function wadToRay(uint256 _wad) internal pure returns (uint256) {
        return mul(_wad, 10**9);
    }

    // Go from wei to ray (10**27)
    function weiToRay(uint256 _wei) internal pure returns (uint256) {
        return mul(_wei, 10**27);
    }

    // The most accurate way to calculate inflation is a loop with
    // for (let i = 0; i < secsPassed; i++) {
    //  `tokenPrice -= tokenPrice * inflRatePerSec`
    // }
    // but this is too slow and expencive so this is an algorithm that has a very small precision error.
    // The magic formula from https://medium.com/coinmonks/math-in-solidity-part-4-compound-interest-512d9e13041b
    function accrueInflation(
        uint256 _principal,
        uint256 _rate,
        uint256 _age
    ) internal pure returns (uint256) {
        return rdiv(_principal, rpow(_rate, _age));
    }

    /**
     * @dev Uses an approximation of continuously compounded interest
     * (discretely compounded every second)
     * @param _principal The principal to calculate the interest on.
     *   Accepted in wei.
     * @param _rate The interest rate. Accepted as a ray representing
     *   1 + the effective interest rate per second, compounded every
     *   second. As an example:
     *   I want to accrue interest at a nominal rate (i) of 5.0% per year
     *   compounded continuously. (Effective Annual Rate of 5.127%).
     *   This is approximately equal to 5.0% per year compounded every
     *   second (to 8 decimal places, if max precision is essential,
     *   calculate nominal interest per year compounded every second from
     *   your desired effective annual rate). Effective Rate Per Second =
     *   Nominal Rate Per Second compounded every second = Nominal Rate
     *   Per Year compounded every second * conversion factor from years
     *   to seconds
     *   Effective Rate Per Second = 0.05 / (365 days/yr * 86400 sec/day) = 1.5854895991882 * 10 ** -9
     *   The value we want to send this function is
     *   1 * 10 ** 27 + Effective Rate Per Second * 10 ** 27
     *   = 1000000001585489599188229325
     *   This will return 5.1271096334354555 Dai on a 100 Dai principal
     *   over the course of one year (31536000 seconds)
     * @param _age The time period over which to accrue interest. Accepted
     *   in seconds.
     * @return The new principal as a wad. Equal to original principal +
     *   interest accrued
     */
    function accrueInterest(
        uint256 _principal,
        uint256 _rate,
        uint256 _age
    ) internal pure returns (uint256) {
        return rmul(_principal, rpow(_rate, _age));
    }

    /**
     * @dev Takes in the desired nominal interest rate per year, compounded
     *   every second (this is approximately equal to nominal interest rate
     *   per year compounded continuously). Returns the ray value expected
     *   by the accrueInterest function
     * @param _rateWad A wad of the desired nominal interest rate per year,
     *   compounded continuously. Converting from ether to wei will effectively
     *   convert from a decimal value to a wad.
     *   So 5% rate should be input as yearlyRateToRay( 0.05 ether )
     * @return 1 * 10 ** 27 + Effective Interest Rate Per Second * 10 ** 27
     */

    function yearlyRateToPerSec(uint256 _rateWad)
        internal
        pure
        returns (uint256)
    {
        return
            add(
                wadToRay(1 ether),
                rdiv(wadToRay(_rateWad), weiToRay(365 * 86400))
            );
    }
}

